import React from 'react';
import { NavLink } from 'react-router-dom';
import styled from 'styled-components';

const StyledWrapper = styled.nav`
  position: fixed;
  top: 0;
  width: 100%;
  max-width: 1000px;
  height: 100px;
  background-color: ${({ theme }) => theme.yellow};
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 30px;
  z-index: 9999;
`;

const StyledLogoLink = styled(NavLink)`
  display: block;
  text-decoration: none;
  font-size: ${({ theme }) => theme.fontSize.xl};
  color: black;
`;

const StyledLinksList = styled.ul`
  margin: 0;
  padding: 0;
  list-style: none;
  display: flex;
`;

const StyledLink = styled.h2`
  margin: 0;
  padding: 0;
  list-style: none;
  text-decoration: none;
  padding: 20px;
  font-size: ${({ theme }) => theme.fontSize.m};
  color: #666;

  &.active {
    color: black;
  }
`;

const NavBar = () => (
  <StyledWrapper>
    <StyledLogoLink to="/">Olx.app</StyledLogoLink>
    <StyledLinksList>
      <li>
        <StyledLink as={NavLink} to="/" activeclass="active">
          Homepage
        </StyledLink>
      </li>
      <li>
        <StyledLink as={NavLink} to="/posts/new" activeclass="active">
          Add post
        </StyledLink>
      </li>
      <li>
        <StyledLink as={NavLink} to="/posts" activeclass="active">
          See posts
        </StyledLink>
      </li>
    </StyledLinksList>
  </StyledWrapper>
);

export default NavBar;
