import React from 'react';
import { data } from 'redux/initialState';

const StyledWrapper = styled.div`
  width: 100%;
  padding: 2vw 10vw 0 4vw;
  border-bottom: 0.5px solid #666;

  &:hover {
    transition: all 0.3s ease-in-out;
    transform: scale(1.02);
  }
`;

const StyledTitle = styled.h1`
  font-weight: ${({ theme }) => theme.bold};
  font-size: ${({ theme }) => theme.fontSize.l};
`;

const StyledAuthor = styled.p`
  color: #666;
  font-size: ${({ theme }) => theme.fontSize.m};
`;

const StyledContent = styled.p`
  color: #666;
  font-size: ${({ theme }) => theme.fontSize.s};
`;

const StyledButton = styled(NavLink)`
  display: block;
  color: #666;
  margin: 10px 0 20px 10px;
  height: 46px;
  width: 100px;
  background-color: transparent;
  outline: none;
  border: 1px solid ${({ theme }) => theme.yellow};
  border-radius: 10px;
  font-size: ${({ theme }) => theme.fontSize.s};
  cursor: pointer;
  text-decoration: none;
  text-align: center;
  padding-top: 13px;
`;

const SinglePostPage = props => {
  const thePost = data.filter(function(item) {
    return item.id === props.match.params.id;
  });

  const { title, author, content } = thePost;

  console.log(thePost);
  return (
    <StyledWrapper>
      <StyledTitle>{title}</StyledTitle>
      <StyledAuthor>{author}</StyledAuthor>
      <StyledContent>{ReactHtmlParser(content)}</StyledContent>
    </StyledWrapper>
  );
};

export default SinglePostPage;
