import styled from 'styled-components';

const Input = styled.input`
  padding: 15px;
  border: 0.5px solid ${({ theme }) => theme.black};
  margin: 20px;
  border-radius: 50px;
  width: 300px;
  outline: none;
  letter-spacing: 3px;

  ::placeholder {
    text-transform: uppercase;
    letter-spacing: 1px;
  }
`;

export default Input;
