import styled from 'styled-components';

const Button = styled.button`
  color: ${({ theme }) => theme.grey400};
  margin: 20px auto;
  width: 220px;
  height: 47px;
  border: 1px solid ${({ theme }) => theme.yellow};
  border-radius: 50px;
  font-size: ${({ theme }) => theme.fontSize.s};
  text-transform: uppercase;
  letter-spacing: 4px;
  outline: none;
`;

export default Button;
