import React from 'react';
import { connect } from 'react-redux';
import { addPostAction } from 'redux/postsRedux';
import { getId } from 'utils/getId';

class Form extends React.Component {
  state = {
    post: {
      id: '',
      title: '',
      author: '',
      content: '',
    },
  };

  handleChange = e => {
    const { post } = this.state;
    this.setState({ post: { ...post, [e.target.name]: e.target.value } });
  };

  handleSubmit = e => {
    const { addPost } = this.props;
    const { post } = this.state;
    const id = getId();
    this.setState({ post: { ...post, id: id } });

    e.preventDefault();
    addPost(post);
  };

  render() {
    const { post } = this.state;

    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Title:
          <input
            name="title"
            label="Title"
            type="text"
            value={post.title}
            onChange={this.handleChange}
          />
        </label>
        <label>
          Author:
          <input
            name="author"
            label="Author"
            type="text"
            value={post.author}
            onChange={this.handleChange}
          />
        </label>
        <label>
          Content:
          <input
            name="content"
            label="Content"
            type="text"
            value={post.content}
            onChange={this.handleChange}
          />
        </label>
        <button type="submit" />
      </form>
    );
  }
}

const mapDispatchToProps = dispatch => ({
  addPost: post => dispatch(addPostAction(post)),
});

export default connect(null, mapDispatchToProps)(Form);
