import styled from 'styled-components';

const Input = styled.input`
  padding: 15px 50px;

  border: 0.5px solid #666;
  margin: 20px;
  border-radius: 50px;
  width: 300px;
  outline: none;

  ::placeholder {
    text-transform: uppercase;
    letter-spacing: 1px;
  }
`;

export default Input;
