import React from 'react';
import styled from 'styled-components';
import ReactHtmlParser from 'react-html-parser';
import { connect } from 'react-redux';
import { loadSinglePostRequest, getSinglePost } from 'redux/postsRedux';

const StyledWrapper = styled.div`
  width: 100%;
  padding: 2vw 10vw 0 4vw;
  border-bottom: 0.5px solid #666;

  &:hover {
    transition: all 0.3s ease-in-out;
    transform: scale(1.02);
  }
`;

const StyledTitle = styled.h1`
  font-weight: ${({ theme }) => theme.bold};
  font-size: ${({ theme }) => theme.fontSize.l};
`;

const StyledAuthor = styled.p`
  color: #666;
  font-size: ${({ theme }) => theme.fontSize.m};
`;

const StyledContent = styled.p`
  color: #666;
  font-size: ${({ theme }) => theme.fontSize.s};
`;

class SinglePost extends React.Component {
  // const thePost = data.filter(function(item) {
  //   return item.id === props.match.params.id;
  // });

  // const { title, author, content } = thePost[0];

  // console.log(thePost);
  componentDidMount() {
    const { loadSinglePost } = this.props;
    loadSinglePost(this.match.params.id);
  }

  render() {
    const { post } = this.props;
    console.log(post);

    return (
      <StyledWrapper>
        {/* <StyledTitle>{title}</StyledTitle>
        <StyledAuthor>{author}</StyledAuthor>
        <StyledContent>{ReactHtmlParser(content)}</StyledContent> */}
      </StyledWrapper>
    );
  }
}

const mapStateToProps = state => ({
  post: getSinglePost(state),
});

const mapDispatchToProps = state => ({
  loadSinglePost: id => loadSinglePostRequest(id),
});

export default connect(mapStateToProps, mapDispatchToProps)(SinglePost);
