import React from 'react';
import { NavLink } from 'react-router-dom';
import styled from 'styled-components';

const StyledWrapper = styled.nav`
  position: fixed;
  top: 0;
  width: 100%;
  max-width: 1000px;
  height: 100px;
  background-color: ${({ theme }) => theme.yellow};
  display: flex;
  align-items: center;
  justify-content: flex-end;
  padding: 0 30px;
  z-index: 9999;
`;

const StyledLinksList = styled.ul`
  margin: 0;
  padding: 0;
  list-style: none;
  display: flex;
`;

const StyledLink = styled.h2`
  margin: 0;
  padding: 0;
  list-style: none;
  text-decoration: none;
  margin: 0 20px;
  font-size: ${({ theme }) => theme.fontSize.m};
  color: ${({ theme }) => theme.grey400};
  font-weight: ${({ theme }) => theme.light};

  &.active {
    color: ${({ theme }) => theme.black};
    border-bottom: 0.5px solid ${({ theme }) => theme.black};
  }
`;

const NavBar = () => (
  <StyledWrapper>
    <StyledLinksList>
      <li>
        <StyledLink as={NavLink} exact to="/" activeclass="active">
          home
        </StyledLink>
      </li>
      <li>
        <StyledLink as={NavLink} exact to="/posts/new" activeclass="active">
          add post
        </StyledLink>
      </li>
      <li>
        <StyledLink as={NavLink} exact to="/" activeclass="active">
          login
        </StyledLink>
      </li>
    </StyledLinksList>
  </StyledWrapper>
);

export default NavBar;
