import React from 'react';
import { connect } from 'react-redux';
// import { addPostAction } from 'redux/postsRedux';
import { getId } from 'utils/getId';
import Input from 'components/common/Input/Input';
import Form from 'components/features/Form/Form';
import Button from 'components/common/Button/Button';

class PostAdd extends React.Component {
  state = {
    post: {
      id: '',
      title: '',
      author: '',
      content: '',
    },
  };

  handleChange = e => {
    const { post } = this.state;
    this.setState({ post: { ...post, [e.target.name]: e.target.value } });
  };

  handleSubmit = e => {
    const { addPost } = this.props;
    const { post } = this.state;
    this.setState({ post: { ...post, id: getId() } });

    e.preventDefault();
    // addPost(post);
  };

  render() {
    const { post } = this.state;

    return (
      <Form onSubmit={this.handleSubmit}>
        <Input
          name="title"
          label="Title"
          type="text"
          placeholder="title"
          value={post.title}
          onChange={this.handleChange}
        />
        <Input
          name="author"
          label="Author"
          type="text"
          placeholder="author"
          value={post.author}
          onChange={this.handleChange}
        />
        <Input
          name="content"
          label="Content"
          type="text"
          placeholder="content"
          value={post.content}
          onChange={this.handleChange}
        />
        <Button type="submit">add post</Button>
      </Form>
    );
  }
}

const mapDispatchToProps = dispatch => ({
  addPost: post => dispatch(addPostAction(post)),
});

export default connect(null, mapDispatchToProps)(PostAdd);
