import styled from 'styled-components';

const Button = styled.button`
  color: ${({ theme }) => theme.grey400};
  padding: 0;
  margin: 20px;
  width: 220px;
  height: 47px;
  border: 1 solid ${({ theme }) => theme.black};
  border-radius: 50px;
  font-size: ${({ theme }) => theme.fontSize.s};
  text-transform: uppercase;
  letter-spacing: 4px;
`;

export default Button;
