import React from 'react';

class Form extends React.Component {
  state = {
    post: {
      name: '',
      content: '',
      author: '',
    },
  };

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input type="text" value={this.state.post.name} onChange={this.handleChange} />
        </label>
        <button type="submit" />
      </form>
    );
  }
}

export default Form;
