import styled from 'styled-components';

const Input = styled.input`
  padding: 15px;
  border: 1px solid ${({ theme }) => theme.black};
  margin: 20px;
  border-radius: 50px;
  width: 300px;
  outline: none;

  ::placeholder {
    text-transform: uppercase;
    letter-spacing: 2px;
  }
`;

export default Input;
